class Button extends React.Component {
  render() {
    return (
      <button onClick={this.props.onClick}>
        {this.props.text}
      </button>
    );
  }
}

class SubmitButton extends Button {
  render() {
    return (
      <Button onClick={this.props.onSubmit} text="Submit" />
    );
  }
}
